// IMAP Email Data Normalizer
// Converts IMAP email format to FloWorx standard format

const item = $json;

function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

// Extract email from address format: "Name <email@domain.com>" or "email@domain.com"
function extractEmail(address) {
  if (!address) return null;
  const match = address.match(/<([^>]+)>/);
  return match ? match[1] : address.trim();
}

function extractName(address) {
  if (!address) return null;
  const match = address.match(/^([^<]+)</);
  return match ? match[1].trim().replace(/"/g, '') : null;
}

// IMAP emails come in different formats depending on server
const messageBody = htmlToText(item.html || item.textHtml || item.text);
const fromAddress = item.from?.value?.[0]?.address || extractEmail(item.from);
const toAddress = item.to?.value?.[0]?.address || extractEmail(item.to);

// Generate consistent IDs
const messageId = item.headers?.['message-id'] || item.uid || `imap-${Date.now()}-${Math.random()}`;
const threadId = item.headers?.['in-reply-to'] || item.headers?.references || messageId;

return {
  json: {
    // Standard FloWorx email format
    id: item.uid || messageId,
    threadId: threadId,
    subject: item.subject || '(No Subject)',
    from: fromAddress,
    fromName: extractName(item.from) || fromAddress,
    to: toAddress,
    toName: extractName(item.to) || toAddress,
    date: item.date || new Date().toISOString(),
    body: messageBody || item.text || '',
    bodyHtml: item.html || item.textHtml || null,

    // IMAP-specific data
    provider: 'imap',
    uid: item.uid,
    mailbox: 'INBOX',
    flags: item.flags || [],

    // Attachment info
    hasAttachments: !!(item.attachments && item.attachments.length > 0),
    attachments: item.attachments || [],

    // Size
    sizeEstimate: item.size || 0,

    // Headers for threading
    messageId: messageId,
    inReplyTo: item.headers?.['in-reply-to'] || null,
    references: item.headers?.references || null,

    // Full headers (for debugging)
    headers: item.headers || {}
  }
};

