// Parse AI Classification Output
// Handles various LLM output formats and attaches email metadata

const outputs = [];
const aiItems = $input.all();
const emailItems = $('Prepare Email Data').all();

for (let i = 0; i < aiItems.length; i++) {
  const aiOutput = aiItems[i].json.output;

  try {
    // Normalize to JSON string
    let clean = typeof aiOutput === 'string' ? aiOutput.trim() : JSON.stringify(aiOutput);

    // Remove markdown code fences (common LLM quirk)
    clean = clean.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/, '');

    // Trim after last closing brace
    const lastBrace = clean.lastIndexOf('}');
    if (lastBrace !== -1) clean = clean.slice(0, lastBrace + 1);

    const parsedOutput = JSON.parse(clean);

    // Attach email metadata from previous step
    const email = emailItems[Math.min(i, emailItems.length - 1)]?.json || {};
    if (email.id) parsedOutput.id = email.id;
    if (email.uid) parsedOutput.uid = email.uid;
    if (email.threadId) parsedOutput.threadId = email.threadId;
    if (email.from) parsedOutput.from = email.from;
    if (email.subject) parsedOutput.subject = email.subject;
    parsedOutput.provider = 'imap';

    outputs.push({ json: { parsed_output: parsedOutput, error: false } });

  } catch (e) {
    // ERROR HANDLING: Log and route to MISC folder
    const email = emailItems[Math.min(i, emailItems.length - 1)]?.json || {};
    console.error('AI Classification Parse Error:', e.message, '| Raw output:', aiOutput);

    outputs.push({
      json: {
        error: true,
        id: email.id,
        uid: email.uid,
        threadId: email.threadId,
        from: email.from,
        subject: email.subject,
        provider: 'imap',
        errorMessage: e.message,
        originalOutput: aiOutput,
        // Fallback classification
        parsed_output: {
          primary_category: 'MISC',
          secondary_category: 'Unclassified',
          confidence: 0.3,
          ai_can_reply: false,
          summary: 'Error parsing AI classification'
        }
      }
    });
  }
}

return outputs;

// Generate IMAP Folder Path
// Maps AI categories to IMAP folder structure
// Note: IMAP uses folders, not labels (no multi-category support)

const FOLDER_MAP = <<<LABEL_MAP>>>;

const classification = $json.parsed_output || $json;
const primaryCategory = classification.primary_category?.toUpperCase() || 'MISC';
const secondaryCategory = classification.secondary_category || null;
const tertiaryCategory = classification.tertiary_category || null;

// IMAP folder path (uses "/" as separator, e.g., "SALES/NewInquiry")
let folderPath = primaryCategory;

if (secondaryCategory && FOLDER_MAP[primaryCategory]?.subfolders?.[secondaryCategory]) {
  folderPath = `${primaryCategory}/${secondaryCategory}`;

  if (tertiaryCategory && FOLDER_MAP[primaryCategory]?.subfolders?.[secondaryCategory]?.subfolders?.[tertiaryCategory]) {
    folderPath = `${primaryCategory}/${secondaryCategory}/${tertiaryCategory}`;
  }
}

// For manager/supplier emails, use specific folders
if (primaryCategory === 'MANAGER' && classification.matched_manager?.name) {
  folderPath = `MANAGER/${classification.matched_manager.name}`;
} else if (primaryCategory === 'SUPPLIERS' && tertiaryCategory) {
  folderPath = `SUPPLIERS/${tertiaryCategory}`;
}

console.log('ğŸ“ IMAP Folder Path:', folderPath, '| Category:', primaryCategory, '>', secondaryCategory, '>', tertiaryCategory);

return {
  json: {
    ...classification,
    folder_path: folderPath,
    primary_folder: primaryCategory,
    full_classification: `${primaryCategory} > ${secondaryCategory || 'General'}`,
    imap_move_required: true
  }
};

// Route to Manager
// Intelligently matches emails to the right manager based on content, roles, and priority

const classification = $json.parsed_output || $json;
const emailData = $('Prepare Email Data').first()?.json || {};

// CRITICAL: Load team configuration from deployment
const TEAM_CONFIG = <<<TEAM_CONFIG>>>;

// Extract managers and suppliers from config
const managers = TEAM_CONFIG.managers || [];
const suppliers = TEAM_CONFIG.suppliers || [];

console.log('ğŸ¯ Manager Routing | Primary Category:', classification.primary_category, '| Team Size:', managers.length);

// Helper: Role configuration
function getRoleConfig(roleId) {
  const configs = {
    'sales_manager': {
      categories: ['SALES'],
      keywords: ['quote', 'price', 'cost', 'buy', 'purchase', 'pricing', 'estimate'],
      weight: 10
    },
    'service_manager': {
      categories: ['SUPPORT', 'URGENT'],
      keywords: ['repair', 'fix', 'broken', 'not working', 'appointment', 'emergency'],
      weight: 10
    },
    'operations_manager': {
      categories: ['MANAGER', 'SUPPLIERS'],
      keywords: ['vendor', 'supplier', 'order', 'inventory', 'hiring'],
      weight: 8
    },
    'support_lead': {
      categories: ['SUPPORT'],
      keywords: ['help', 'question', 'how to', 'parts'],
      weight: 6
    },
    'owner': {
      categories: ['MANAGER', 'URGENT'],
      keywords: ['legal', 'strategic', 'partnership', 'important'],
      weight: 10
    }
  };
  return configs[roleId] || { categories: [], keywords: [], weight: 0 };
}

let matchedManager = null;
let routingReason = '';
let routingConfidence = 0;

// PRIORITY 1: Name mentioned in email
const emailBody = emailData.body?.toLowerCase() || '';
const emailSubject = emailData.subject?.toLowerCase() || '';
const fullEmailText = `${emailSubject} ${emailBody}`;

for (const manager of managers) {
  const fullName = manager.name.toLowerCase();
  const firstName = fullName.split(' ')[0];

  if (fullEmailText.includes(fullName) || fullEmailText.includes(firstName)) {
    matchedManager = manager;
    routingReason = `Name mentioned: "${manager.name}"`;
    routingConfidence = 100;
    console.log(`âœ… Priority 1: Name Match - ${manager.name}`);
    break;
  }
}

// PRIORITY 2: Category + Role match
if (!matchedManager) {
  const primaryCategory = classification.primary_category?.toUpperCase() || '';
  const scores = [];

  for (const manager of managers) {
    let score = 0;
    const managerRoles = Array.isArray(manager.roles) ? manager.roles : (manager.role ? [manager.role] : []);
    const matchedRoles = [];

    for (const roleId of managerRoles) {
      const roleConfig = getRoleConfig(roleId);
      if (roleConfig.categories.includes(primaryCategory)) {
        score += roleConfig.weight;
        matchedRoles.push(roleId);
      }
    }

    if (score > 0) {
      scores.push({ manager, score, reason: `Category match: ${primaryCategory}`, matchedRoles });
    }
  }

  if (scores.length > 0) {
    scores.sort((a, b) => b.score - a.score);
    const winner = scores[0];
    matchedManager = winner.manager;
    routingReason = winner.reason;
    routingConfidence = Math.min(95, 70 + winner.score);
    console.log(`âœ… Priority 2: Category Match - ${winner.manager.name} (score: ${winner.score})`);
  }
}

// PRIORITY 3: Content analysis for MANAGER category
if (!matchedManager && classification.primary_category?.toUpperCase() === 'MANAGER') {
  const scores = [];

  for (const manager of managers) {
    let score = 0;
    const managerRoles = Array.isArray(manager.roles) ? manager.roles : (manager.role ? [manager.role] : []);
    const matchedKeywords = [];

    for (const roleId of managerRoles) {
      const roleConfig = getRoleConfig(roleId);
      for (const keyword of roleConfig.keywords) {
        if (fullEmailText.includes(keyword.toLowerCase())) {
          score += 2;
          matchedKeywords.push(keyword);
        }
      }
      score += roleConfig.weight * 0.1;
    }

    if (score > 0) {
      scores.push({ manager, score, reason: `Content analysis: ${matchedKeywords.slice(0, 3).join(', ')}` });
    }
  }

  if (scores.length > 0) {
    scores.sort((a, b) => b.score - a.score);
    const winner = scores[0];
    matchedManager = winner.manager;
    routingReason = winner.reason;
    routingConfidence = Math.min(85, 50 + winner.score * 2);
    console.log(`âœ… Priority 3: Content Analysis - ${winner.manager.name}`);
  }
}

// PRIORITY 4: Supplier detection
if (!matchedManager && suppliers.length > 0) {
  const senderEmail = emailData.from?.toLowerCase() || '';
  const senderDomain = senderEmail.split('@')[1] || '';

  for (const supplier of suppliers) {
    const supplierEmail = supplier.email?.toLowerCase() || '';
    if (senderEmail === supplierEmail || (supplier.domain && senderDomain.includes(supplier.domain))) {
      const opsManager = managers.find(m => {
        const roles = Array.isArray(m.roles) ? m.roles : (m.role ? [m.role] : []);
        return roles.includes('operations_manager');
      });

      if (opsManager) {
        matchedManager = opsManager;
        routingReason = `Supplier email: ${supplier.name}`;
        routingConfidence = 90;
        console.log(`âœ… Priority 4: Supplier - ${opsManager.name}`);
        break;
      }
    }
  }
}

// FALLBACK: Default to first manager
if (!matchedManager && managers.length > 0) {
  matchedManager = managers[0];
  routingReason = 'Default routing (no specific match)';
  routingConfidence = 30;
  console.log(`âš ï¸ Fallback: Default to ${matchedManager.name}`);
}

// Output with routing decision
return {
  json: {
    ...classification,
    matched_manager: matchedManager,
    routing_decision: {
      manager_name: matchedManager?.name || 'Unassigned',
      manager_email: matchedManager?.email || null,
      matched_roles: Array.isArray(matchedManager?.roles) ? matchedManager.roles : (matchedManager?.role ? [matchedManager.role] : []),
      routing_reason: routingReason,
      routing_confidence: routingConfidence,
      timestamp: new Date().toISOString()
    },
    manager_folder: matchedManager?.name ? `MANAGER/${matchedManager.name}` : 'MANAGER/Unassigned'
  }
};

