// Electrical business email processing
const item = $json;

function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

const messageBody = htmlToText(item.html);
const messageId = item.headers?.['message-id'] || null;

// Electrical-specific content analysis
const electricalKeywords = [
  'electrician', 'electrical', 'wiring', 'outlet', 'switch', 'panel', 'circuit', 'breaker',
  'GFCI', 'AFCI', 'lighting', 'fixture', 'receptacle', 'installation', 'repair', 'upgrade'
];

const emergencyKeywords = [
  'urgent', 'emergency', 'spark', 'shock', 'fire', 'smoke', 'asap', 'immediately',
  'tripping', 'faulty', 'dangerous'
];

const hasElectricalContent = electricalKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

const isEmergency = emergencyKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

return {
  json: {
    id: item.id,
    threadId: item.threadId,
    subject: item.subject,
    from: item.from?.value?.[0]?.address || null,
    fromName: item.from?.value?.[0]?.name || null,
    to: item.to?.value?.[0]?.address || null,
    toName: item.to?.value?.[0]?.name || null,
    date: item.date,
    body: messageBody,
    bodyHtml: item.html,
    labels: item.labelIds,
    sizeEstimate: item.sizeEstimate,
    messageId: messageId,
    hasElectricalContent,
    isEmergency,
    businessType: 'Electrician'
  }
};

