// Flooring contractor business email processing
const item = $json;

function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

const messageBody = htmlToText(item.html);
const messageId = item.headers?.['message-id'] || null;

// Flooring-specific content analysis
const flooringKeywords = [
  'flooring', 'floor', 'hardwood', 'tile', 'carpet', 'vinyl', 'laminate',
  'installation', 'refinishing', 'repair', 'refinish', 'sand', 'stain',
  'varnish', 'seal', 'grout', 'subfloor', 'underlayment', 'baseboard',
  'transition', 'threshold', 'commercial flooring', 'estimate'
];

const emergencyKeywords = [
  'urgent', 'emergency', 'water damage', 'flooding', 'leak', 'broken',
  'asap', 'immediately', 'damaged', 'warped', 'buckled'
];

const hasFlooringContent = flooringKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

const isEmergency = emergencyKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

return {
  json: {
    id: item.id,
    threadId: item.threadId,
    subject: item.subject,
    from: item.from?.value?.[0]?.address || null,
    fromName: item.from?.value?.[0]?.name || null,
    to: item.to?.value?.[0]?.address || null,
    toName: item.to?.value?.[0]?.name || null,
    date: item.date,
    body: messageBody,
    bodyHtml: item.html,
    labels: item.labelIds,
    sizeEstimate: item.sizeEstimate,
    messageId: messageId,
    hasFlooringContent,
    isEmergency,
    businessType: 'Flooring'
  }
};

