// Roofing contractor business email processing
const item = $json;

function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

const messageBody = htmlToText(item.html);
const messageId = item.headers?.['message-id'] || null;

// Roofing-specific content analysis
const roofingKeywords = [
  'roofing', 'roof', 'shingle', 'tile', 'gutter', 'ventilation', 'attic',
  'repair', 'replacement', 'inspection', 'weather damage', 'storm', 'leak',
  'gutters', 'downspouts', 'flashing', 'ventilation', 'insulation', 'estimate',
  'quote', 'commercial roofing', 'residential roofing', 'maintenance'
];

const emergencyKeywords = [
  'urgent', 'emergency', 'leak', 'storm damage', 'hail damage', 'wind damage',
  'asap', 'immediately', 'water damage', 'collapsed', 'missing shingles'
];

const hasRoofingContent = roofingKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

const isEmergency = emergencyKeywords.some(keyword => 
  messageBody.toLowerCase().includes(keyword)
);

return {
  json: {
    id: item.id,
    threadId: item.threadId,
    subject: item.subject,
    from: item.from?.value?.[0]?.address || null,
    fromName: item.from?.value?.[0]?.name || null,
    to: item.to?.value?.[0]?.address || null,
    toName: item.to?.value?.[0]?.name || null,
    date: item.date,
    body: messageBody,
    bodyHtml: item.html,
    labels: item.labelIds,
    sizeEstimate: item.sizeEstimate,
    messageId: messageId,
    hasRoofingContent,
    isEmergency,
    businessType: 'Roofing'
  }
};

