// Get the input text.
// It's generally safer to get the input directly from the incoming item
// rather than referencing a specific node name if not strictly necessary.
// Replace 'body' with the actual field name where your email content (or relevant text) resides.
// Common field names could be 'subject', 'html', or 'snippet' depending on your previous node's output.
const inputText = $('AI Master Classifier').first().json.output || ''; // Fallback to empty string if 'body' is null/undefined

// Regular expression to find common phone number formats:
// (XXX) XXX-XXXX, XXX-XXX-XXXX, XXX.XXX.XXXX, XXX XXX XXXX
// It uses non-capturing groups and makes separators optional.
const phoneNumberRegex = /\(?\d{3}\)?[-.\s]?\d{3}[-.\s]?\d{4}/;

const match = inputText.match(phoneNumberRegex);

let extractedPhoneNumber = null;
let cleanedPhoneNumber = null;

if (match && match[0]) {
  extractedPhoneNumber = match[0];

  // Optional: Clean the extracted number to a consistent format (e.g., remove parentheses, spaces, hyphens)
  // This is often desired for dialing or sending SMS.
  cleanedPhoneNumber = extractedPhoneNumber.replace(/\D/g,'').replace(/^(\d{10})$/, '+1$1'); // Removes all non-digit characters
}

// If you need to extract multiple phone numbers, you can use:
// const allMatches = inputText.match(new RegExp(phoneNumberRegex, 'g'));
// In that case, you would iterate over allMatches and clean each.

// Set the extracted and cleaned phone numbers as new fields in the output
return { 
  json: { 
    extractedPhoneNumber: extractedPhoneNumber, // Original format extracted
    cleanedPhoneNumber: cleanedPhoneNumber     // Cleaned, digits-only format
  } 
};

