const outputs = [];

const aiItems = $input.all();
const gmailItems = $('Gmail Trigger1').all();

for (let i = 0; i < aiItems.length; i++) {
  const aiOutput = aiItems[i].json.output;

  try {
    // normalize to a JSON string
    let clean = typeof aiOutput === 'string' ? aiOutput.trim() : JSON.stringify(aiOutput);

    // strip ``` or ```json fences
    clean = clean.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/, '');

    // trim anything after the last closing brace (common LLM quirk)
    const lastBrace = clean.lastIndexOf('}');
    if (lastBrace !== -1) clean = clean.slice(0, lastBrace + 1);

    const parsedOutput = JSON.parse(clean);

    // align with the same Gmail item index (fallback to last if sizes differ)
    const g = gmailItems[Math.min(i, gmailItems.length - 1)]?.json || {};

    // attach Gmail IDs
    if (g.id) parsedOutput.id = g.id;
    if (g.threadId) parsedOutput.threadId = g.threadId;

    // This is the output for a SUCCESSFUL run
    outputs.push({ json: { parsed_output: parsedOutput, error: false } });

  } catch (e) {
    // THIS BLOCK ONLY RUNS IF THERE IS AN ERROR
    // It now correctly finds the original email and includes its ID
    const g = gmailItems[Math.min(i, gmailItems.length - 1)]?.json || {};
    outputs.push({ 
      json: { 
        error: true, 
        id: g.id, 
        errorMessage: e.message, 
        originalOutput: aiOutput 
      } 
    });
  }
}

return outputs;

// This code is designed to work with n8n's simplified Gmail Trigger output.

const item = $json;

// --- HELPER FUNCTION: HTML to Clean Text ---
function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

// --- EXTRACT CORE DATA ---
const messageBody = htmlToText(item.html);
const messageId = item.headers?.['message-id'] || null;

// --- ASSEMBLE FINAL JSON ---
// This is the missing part. It must return a single object, not an array.
return {
  json: {
    id: item.id,
    threadId: item.threadId,
    subject: item.subject,
    from: item.from?.value?.[0]?.address || null,
    fromName: item.from?.value?.[0]?.name || null,
    to: item.to?.value?.[0]?.address || null,
    toName: item.to?.value?.[0]?.name || null,
    date: item.date,
    body: messageBody,
    bodyHtml: item.html,
    labels: item.labelIds,
    sizeEstimate: item.sizeEstimate,
    messageId: messageId
  }
};

// Replace newlines with <br> for HTML email rendering
return {
  json: {
    output: $json.output
      .replace(/\n/g, '<br>')
  }
};

