// Universal email data normalizer - works with Gmail, Outlook, Yahoo, etc.
const item = $json;

function htmlToText(html) {
  if (!html) return '';
  return html
    .replace(/<script[^>]*>([\S\s]*?)<\/script>/gmi, '')
    .replace(/<style[^>]*>([\S\s]*?)<\/style>/gmi, '')
    .replace(/<!--[\s\S]*?-->/g, '')
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(div|p|h[1-6]|li|tr)>/gi, '\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/(\n\s*){3,}/g, '\n\n')
    .trim();
}

const messageBody = htmlToText(item.html || item.body?.content);
const messageId = item.headers?.['message-id'] || item.internetMessageId || item.id;

return {
  json: {
    id: item.id,
    threadId: item.threadId || item.conversationId,
    subject: item.subject,
    from: item.from?.value?.[0]?.address || item.from?.emailAddress?.address || null,
    fromName: item.from?.value?.[0]?.name || item.from?.emailAddress?.name || null,
    to: item.to?.value?.[0]?.address || item.toRecipients?.[0]?.emailAddress?.address || null,
    toName: item.to?.value?.[0]?.name || item.toRecipients?.[0]?.emailAddress?.name || null,
    date: item.date || item.receivedDateTime,
    body: messageBody,
    bodyHtml: item.html || item.body?.content,
    labels: item.labelIds || [],
    categories: item.categories || [],
    provider: item.labelIds ? 'gmail' : (item.categories !== undefined ? 'outlook' : 'unknown'),
    hasAttachments: !!(item.hasAttachments || (item.attachments?.length > 0)),
    sizeEstimate: item.sizeEstimate || 0,
    messageId: messageId
  }
};

