const outputs = [];

const aiItems = $input.all();
const emailItems = $('Prepare Email Data').all();

for (let i = 0; i < aiItems.length; i++) {
  const aiOutput = aiItems[i].json.output;

  try {
    // Normalize to JSON string
    let clean = typeof aiOutput === 'string' ? aiOutput.trim() : JSON.stringify(aiOutput);

    // Remove markdown fences
    clean = clean.replace(/^```(?:json)?\s*/i, '').replace(/\s*```$/, '');

    // Trim after last closing brace (common LLM quirk)
    const lastBrace = clean.lastIndexOf('}');
    if (lastBrace !== -1) clean = clean.slice(0, lastBrace + 1);

    const parsedOutput = JSON.parse(clean);

    // Attach email metadata
    const email = emailItems[Math.min(i, emailItems.length - 1)]?.json || {};
    if (email.id) parsedOutput.id = email.id;
    if (email.threadId) parsedOutput.threadId = email.threadId;
    if (email.provider) parsedOutput.provider = email.provider;

    outputs.push({ json: { parsed_output: parsedOutput, error: false } });

  } catch (e) {
    // ERROR HANDLING: Critical for production
    const email = emailItems[Math.min(i, emailItems.length - 1)]?.json || {};
    outputs.push({
      json: {
        error: true,
        id: email.id,
        threadId: email.threadId,
        provider: email.provider,
        errorMessage: e.message,
        originalOutput: aiOutput
      }
    });
  }
}

return outputs;

const parsed = $json.parsed_output;
const provider = parsed.provider || 'gmail';

// Dynamic label mapping from database
const labelMap = <<<LABEL_MAP>>>;

// Helper function to normalize category names
function normalizeCategory(category) {
  if (!category) return null;
  return category.toString().toUpperCase().trim();
}

// Helper function to find label with fuzzy matching
function findLabel(category, labelMap) {
  if (!category) return null;

  const normalized = normalizeCategory(category);

  // Try exact match first
  const exactIdMatch = labelMap[normalized]?.id;
  if (exactIdMatch) {
    return exactIdMatch;
  }

  // Try case-insensitive match (on keys)
  const caseInsensitiveKey = Object.keys(labelMap).find(key => 
    key.toUpperCase() === normalized
  );
  if (caseInsensitiveKey) {
    return labelMap[caseInsensitiveKey].id;
  }

  // Try partial match (on keys)
  const partialMatchKey = Object.keys(labelMap).find(key => 
    key.toUpperCase().includes(normalized) || 
    normalized.includes(key.toUpperCase())
  );
  if (partialMatchKey) {
    return labelMap[partialMatchKey].id;
  }

  return null;
}

// CRITICAL FIX: Validate label IDs before using them
function validateLabelId(labelId) {
  if (!labelId) return false;

  // Check if it's a valid Gmail label ID format
  if (typeof labelId === 'string' && labelId.startsWith('Label_')) {
    return true;
  }

  // Check if it's a valid Outlook folder ID format
  if (typeof labelId === 'string' && labelId.startsWith('AAMkAD')) {
    return true;
  }

  return false;
}

const labels = [];
const debugInfo = {
  primaryCategory: parsed.primary_category,
  secondaryCategory: parsed.secondary_category,
  tertiaryCategory: parsed.tertiary_category,
  foundLabelIds: [],
  finalLabels: [],
  labelMapSize: Object.keys(labelMap).length
};

// Add primary category label with improved matching
const primaryLabelId = findLabel(parsed.primary_category, labelMap);
if (primaryLabelId && validateLabelId(primaryLabelId)) {
  labels.push(primaryLabelId);
  debugInfo.foundLabelIds.push(primaryLabelId);
} else {
  console.warn(`⚠️ Invalid or missing primary label for: ${parsed.primary_category}`);
}

// Add secondary category label
if (parsed.secondary_category) {
  const key = `${parsed.primary_category}/${parsed.secondary_category}`;
  const secondaryLabelId = findLabel(key, labelMap);
  if (secondaryLabelId && validateLabelId(secondaryLabelId)) {
    labels.push(secondaryLabelId);
    debugInfo.foundLabelIds.push(secondaryLabelId);
  } else {
    console.warn(`⚠️ Invalid or missing secondary label for: ${key}`);
  }
}

// Add tertiary category label
if (parsed.tertiary_category) {
  const key = `${parsed.primary_category}/${parsed.secondary_category}/${parsed.tertiary_category}`;
  const tertiaryLabelId = findLabel(key, labelMap);
  if (tertiaryLabelId && validateLabelId(tertiaryLabelId)) {
    labels.push(tertiaryLabelId);
    debugInfo.foundLabelIds.push(tertiaryLabelId);
  } else {
    console.warn(`⚠️ Invalid or missing tertiary label for: ${key}`);
  }
}

// Remove duplicates and ensure we have valid label IDs
const uniqueLabels = [...new Set(labels)].filter(labelId => validateLabelId(labelId));

// CRITICAL FIX: Fallback to MISC label ID if no valid labels found
const miscLabelId = labelMap['MISC']?.id || null;
const validMiscLabel = miscLabelId && validateLabelId(miscLabelId) ? miscLabelId : null;

const finalLabels = uniqueLabels.length > 0 ? uniqueLabels : (validMiscLabel ? [validMiscLabel] : []);
debugInfo.finalLabels = finalLabels;

// Log debug information
console.log('🔍 Label Processing Debug:', debugInfo);

// If no valid labels found, log warning and return empty array
if (finalLabels.length === 0) {
  console.warn('⚠️ No valid labels found for email classification. Email will not be labeled.');
  console.warn('📋 Available labels in map:', Object.keys(labelMap));
  console.warn('📋 Parsed categories:', {
    primary: parsed.primary_category,
    secondary: parsed.secondary_category,
    tertiary: parsed.tertiary_category
  });
}

return {
  json: {
    ...parsed,
    labelsToApply: finalLabels,
    categoriesToApply: finalLabels.map(l => {
      // Find category name from label ID for debug purposes
      const entry = Object.entries(labelMap).find(([k, v]) => v.id === l);
      return entry ? entry[0] : l;
    }),
    provider: provider,
    debugInfo: debugInfo
  }
};

// Prepare context for AI reply agent
const emailData = $('Prepare Email Data').first()?.json || {};
const classification = $json.parsed_output || {};
const voiceContext = $('Fetch Voice Context (Optional)').first()?.json || {};

// Handle optional voice training data
let voiceExamples = '';
let voiceMetrics = '';

if (voiceContext && voiceContext.examples && voiceContext.examples.length > 0) {
  voiceExamples = `RECENT STYLE EXAMPLES (How you typically write):
${'─'.repeat(60)}
${voiceContext.examples.map((ex, i) => `Example ${i+1}:\n${ex}`).join('\n\n')}`;

  if (voiceContext.metrics) {
    voiceMetrics = `\nVOICE METRICS:
Formality: ${voiceContext.metrics.formality || 'N/A'}
Empathy: ${voiceContext.metrics.empathy || 'N/A'}
Directness: ${voiceContext.metrics.directness || 'N/A'}`;
  }
} else {
  voiceExamples = `VOICE TRAINING: Not yet available (will learn from your sent emails)`;
}

return {
  json: {
    emailSubject: emailData.subject,
    emailFrom: emailData.from,
    emailBody: emailData.body,
    threadId: emailData.threadId,
    classification: classification,
    voiceExamples: voiceExamples,
    voiceMetrics: voiceMetrics,
    hasVoiceTraining: !!(voiceContext && voiceContext.examples && voiceContext.examples.length > 0)
  }
};

