// Calculate performance metrics
const classificationTime = 3; // minutes to classify email
const responseTime = 8; // minutes to draft response
const receptionistHourlyRate = 25;

const emailsProcessed = 1;
const aiCanReply = $('Parse AI Classification').item.json.parsed_output?.ai_can_reply || false;

// Different time savings based on email type:
// - Labeled only (ai_can_reply: false): 3 minutes
// - Labeled + Drafted (ai_can_reply: true): 3 + 8 = 11 minutes
const avgMinutesPerEmail = aiCanReply ? (classificationTime + responseTime) : classificationTime;
const timeSavedHours = +(emailsProcessed * avgMinutesPerEmail / 60).toFixed(2);
const moneySaved = +(timeSavedHours * receptionistHourlyRate).toFixed(2);

return {
  json: {
    date: new Date().toISOString().slice(0, 10),
    type: aiCanReply ? 'Drafting' : 'Labeling',
    emailsProcessed,
    avgMinutesPerEmail,
    timeSavedHours,
    receptionistHourlyRate,
    moneySaved,
    userId: '<<<USER_ID>>>'
  }
};

